import React, { useState } from 'react';
import '../App.css';
import { Przycisk } from './Przycisk';
import './HeroSection.css';

export default function HeroSection4() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    console.log('Próba logowania');
    e.preventDefault();

    try {
      const response =  fetch('http://192.168.8.148/:8000/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        const data =  response.json(); // Odczytanie odpowiedzi JSON
        localStorage.setItem('token', data.token); // Zapisanie tokena
        console.log('Zalogowano pomyślnie');
        // Tutaj możesz przekierować użytkownika do innej strony lub wykonać inne działania
      } else {
        console.error('Błąd logowania');
      }
    } catch (error) {
      console.error('Błąd:', error);
    }
  };

  return (
    <div className='kontener'>
      <video src='/filmy/video1.mp4' autoPlay loop muted />
      <h1>Logowanie</h1>

      <div className='hero-btns'>
      <form onSubmit={handleLogin}>
  <input
    className='username-input'
    name='username'
    type='text'
    placeholder='Nazwa użytkownika'
    value={username}
    onChange={(e) => setUsername(e.target.value)}
  />
  <input
    className='haslo-input'
    name='haslo'
    type='password'
    placeholder='Hasło'
    value={password}
    onChange={(e) => setPassword(e.target.value)}
  />
  <Przycisk type='submit' stylPrzycisku='btn--outline'>
    Zaloguj się
  </Przycisk>
</form>
      </div>
    </div>
  );
}
