import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Przycisk } from './Przycisk';
import './Przycisk.css';
import './Menu.css';

function Menu() {
  const [klikniecie, stanKlikniecia] = useState(false);
  const [przycisk, stanPrzycisku] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userGroup, setUserGroup] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
    if (token) {
      fetchUserGroup(token);
    }
  }, []);

  const fetchUserGroup = async (token) => {
    try {
      const response = await fetch('http://20.83.148.157:8000/zarzadzanie_pojazdami/jaka_grupa/', {
        headers: {
          'Authorization': `Token ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setUserGroup(data.grupa[0]); // Ustawiamy grupę użytkownika
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    setIsLoggedIn(false);
    setUserGroup(null);
    window.location.reload();
  };

  const pokazPrzycisk = () => {
    if (window.innerWidth <= 960) {
      stanPrzycisku(false);
    } else {
      stanPrzycisku(true);
    }
  };

  useEffect(() => {
    window.addEventListener('resize', pokazPrzycisk);
    return () => window.removeEventListener('resize', pokazPrzycisk);
  }, []);

  return (
    <nav className='menu'>
      <div className='menu-kontener'>
        <Link to='/' className='menu-logo' onClick={stanKlikniecia.bind(null, false)}>
          LOGO <i className='link-tekst-menu' />
        </Link>
        <div className='menu-ikona' onClick={obslugaKlikniecia}>
          <i className={klikniecie ? 'fas fa-times' : 'fas fa-bars'} />
        </div>
        <ul className={klikniecie ? 'nawigator-aktywny' : 'nawigator-nieaktywny'}>
          <li className='podmiot-nawigatora0'>
            <Link to='/Auta' className='nawigator-linki' onClick={stanKlikniecia.bind(null, false)}>
              Auta
            </Link>
          </li>
          <li className='podmiot-nawigatora0'>
            <Link to='/Informacje' className='nawigator-linki' onClick={stanKlikniecia.bind(null, false)}>
              Informacje
            </Link>
          </li>
          <li className='podmiot-nawigatora0'>
            <Link to='/Kontakt' className='nawigator-linki' onClick={stanKlikniecia.bind(null, false)}>
              Kontakt
            </Link>
          </li>
          {!isLoggedIn && (
            <li className='podmiot-nawigatora1'>
              <Link to='/Rejestracja' className='nav-links-mobile' onClick={stanKlikniecia.bind(null, false)}>
                Zarejestruj się
              </Link>
            </li>
          )}
          {!isLoggedIn && (
            <li className='podmiot-nawigatora1'>
              <Link to='/Logowanie' className='nav-links-mobile' onClick={stanKlikniecia.bind(null, false)}>
                Zaloguj się
              </Link>
            </li>
          )}
          {isLoggedIn && userGroup === 'Klient' && (
            <li className='podmiot-nawigatora1'>
              <Link to='/Zamowienia' className='nav-links-mobile' onClick={stanKlikniecia.bind(null, false)}>
                Twoje Wypożyczenia
              </Link>
            </li>
          )}
          {isLoggedIn && userGroup === 'Pracownik' && (
            <li className='podmiot-nawigatora1'>
              <Link to='/ZarzadzanieWypozyczeniami' className='nav-links-mobile' onClick={stanKlikniecia.bind(null, false)}>
                Zarządzanie Wypożyczeniami
              </Link>
            </li>
          )}
          {isLoggedIn && (
            <li className='podmiot-nawigatora1'>
              <Link to='/' onClick={handleLogout} className='nav-links-mobile'>
                Wyloguj się
              </Link>
            </li>
          )}
        </ul>
        {przycisk && !isLoggedIn && (
          <>
            <Przycisk className='btn--outline' stylPrzycisku='btn--outline' linkPrzycisku='/Rejestracja'>
              Zarejestruj się
            </Przycisk>
            <Przycisk className='btn--outline' stylPrzycisku='btn--outline' linkPrzycisku='/Logowanie'>
              Zaloguj się
            </Przycisk>
          </>
        )}
        {przycisk && isLoggedIn && (
          <>
            {userGroup === 'Klient' && (
              <Przycisk className='btn--outline' stylPrzycisku='btn--outline' linkPrzycisku='/Zamowienia'>
                Twoje Wypożyczenia
              </Przycisk>
            )}
            {userGroup === 'Pracownik' && (
              <Przycisk className='btn--outline' stylPrzycisku='btn--outline' linkPrzycisku='/ZarzadzanieWypozyczeniami'>
                Zarządzanie Wypożyczeniami
              </Przycisk>
            )}
            <Przycisk onClick={handleLogout} className='btn--outline' stylPrzycisku='btn--outline'>
              Wyloguj się
            </Przycisk>
          </>
        )}
      </div>
    </nav>
  );
}

export default Menu;
