import React, { useState, useEffect } from 'react';
import '../App.css';
import { Przycisk } from './Przycisk';
import './HeroSection.css';

const ProductFilter = ({ options, onOptionSelect }) => {
  const [selectedOption, setSelectedOption] = useState('');

  const handleChange = (e) => {
    setSelectedOption(e.target.value);
    onOptionSelect(e.target.value);
  };

  return (
    <select value={selectedOption} onChange={handleChange}>
      <option value="">Wszystkie opcje</option>
      {options.map((option, index) => (
        <option key={index} value={option}>{option}</option>
      ))}
    </select>
  );
};

const VehicleList = ({ vehicles }) => (
  <div>
  {vehicles.map(vehicle => (
    <div key={vehicle.id} className="vehicle">
      <h2>{vehicle.marka} {vehicle.model}</h2>
      <p>Rok produkcji: {vehicle.rok_produkcji}</p>
      <p>Cena: {vehicle.cena}</p>
      {/* Możesz dodać więcej właściwości z obiektu vehicle, jeśli są dostępne i chcesz je wyświetlić */}
      <img src={vehicle.zdjecie1} alt={`Zdjęcie ${vehicle.marka} ${vehicle.model}`} />
      {/* Możesz dodać więcej zdjęć lub innych elementów związanych z pojazdem */}
    </div>
  ))}
</div>
);

export default function HeroSection2() {
  const [vehicles, setVehicles] = useState([]);
  const [brands, setBrands] = useState([]);
  const [types, setTypes] = useState([]);

  useEffect(() => {
    // Pobieranie pojazdów
    fetch('http://localhost:8000/zarzadzanie_pojazdami/wyszukaj_pojazdy/?format=json')
      .then(response => response.json())
      .then(data => setVehicles(data))
      .catch(error => console.error('Error:', error));

    // Pobieranie marek
    fetch('http://localhost:8000/api/brands/')
      .then(response => response.json())
      .then(data => setBrands(data))
      .catch(error => console.error('Error:', error));

    // Pobieranie rodzajów
    fetch('http://localhost:8000/api/types/')
      .then(response => response.json())
      .then(data => setTypes(data))
      .catch(error => console.error('Error:', error));
  }, []);

  const handleOptionSelect = (option) => {
    console.log(`Wybrana opcja: ${option}`);
    // Tutaj możesz dodać logikę filtracji
  };

  return (
    <div className='kontener'>
    <video src='/filmy/video1.mp4' autoPlay loop muted />
    <h1>Auta</h1>

    <div className='hero-btns'>
      <Przycisk className='btn' stylPrzycisku='btn--outline' rozmiarPrzycisku='btn--large' linkPrzycisku='/'>
        Strona Główna
      </Przycisk>

      {/* Filtry */}
      <ProductFilter options={brands} onOptionSelect={handleOptionSelect} />
      <ProductFilter options={types} onOptionSelect={handleOptionSelect} />


      {/* Lista pojazdów */}
      <VehicleList vehicles={vehicles} />
    </div>
  </div>
  );
}
